      // props:{
      //   percentage:''
      // },
    data(){
      return{
        isplay:true,
        audio:false,
        returnColor:{color:'blue'},
        time:0,
        timer:'',
        songresouce:'',
        percent:'',
        parmas:'',
        msg:'',
        resouce:'',
        lyric:'',
        currentLyric:'',
        value10:0,
        value2:70,
        value1:0,
        song_pic:'',
        interval:'',
        voice:'data:image/png;base64,',
        play_icon:'data:image/png;base64,',
        note:{
          background_image:'url(song_pic)'
        }
      }
    },
    components:{
      PageHeader,
    },
    methods: {
      play: function () {
       var that = this
        if(this.audio) {
          this.$refs.music.pause()
          this.audio=false
          this.play_icon = 'data:image/png;base64,'
          clearInterval(this.timer)
        }else{
          this.play_icon ='data:image/png;base64,'
          this.$refs.music.play()
          this.audio=true
          this.$refs.music.currentTime = this.value1

          this.timer=setInterval(function () {
              that.value1 += 100/that.interval
              that.time+=1
          }, 1000)
          }

        },
        select:function () {
          this.$refs.music.currentTime = this.value1* (this.interval/100)
          this.time = parseInt(this.value1 *(this.interval/100))
        },
      changeVoice:function(){
        this.$refs.music.volume = this.value2/100
      },
      mute:function () {
        if(!this.$refs.music.muted) {
          this.$refs.music.muted = true
          this.voice = 'data:image/png;base64,'
        }else{
          this.$refs.music.muted = false
          this.voice = 'data:image/png;base64,'
        }
      }
    },

      // watch(){
      //   if(this.time>this.interval)
      //   this.time =0
      // },

    mounted(){
      this.audio = true
      var that = this

        this.timer = setInterval(function () {
          that.value1 += 100 / that.interval
          that.time += 1
        }, 1000)

      bus.$on('sender',(res)=>{
          that.interval = res.interval
          let lyric =  Base64.decode(res.lyric)

          let currentLyric = new Lyric(lyric,this.handleLyric)
          console.log(currentLyric)
          that.currentLyric = currentLyric.lines
     that.songresouce = "http://dl.stream.qqmusic.qq.com/"+res[0].purl
     that.song_pic ='https://y.gtimg.cn/music/photo_new/T002R300x300M000'+res.album_mid+'.jpg?max_age=2592000 res.album_mid'

     })
    },
    // computed: {
